mapper.select:
select
  id, role_name, role_creator, role_id, role_create_time
from role


        and id = #{id}


        and role_name = #{roleName}


        and role_creator = #{roleCreator}


        and role_id = #{roleId}


        and role_create_time = #{roleCreateTime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from role


        and id = #{id}


        and role_name = #{roleName}


        and role_creator = #{roleCreator}


        and role_id = #{roleId}


        and role_create_time = #{roleCreateTime}
